import React, { useState, useEffect } from 'react';
import TravelPlan from './components/TravelPlan';
import './App.css';

function App() {
  // 登录状态管理
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isGuest, setIsGuest] = useState(true);
  const [showLoginModal, setShowLoginModal] = useState(false);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  // 任务列表状态
  const [tasks, setTasks] = useState([]);
  const [inputValue, setInputValue] = useState('');
  const [completedTasks, setCompletedTasks] = useState([]);
  const [showHistory, setShowHistory] = useState(false);

  // 页面状态 - 将旅游计划设置为首页默认展示
  const [activePage, setActivePage] = useState('travel');

  // 笑话列表状态
  const [jokes, setJokes] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const jokesPerPage = 10;

  // 初始化任务列表和已完成任务
  useEffect(() => {
    const savedTasks = localStorage.getItem('tasks');
    const savedCompletedTasks = localStorage.getItem('completedTasks');

    if (savedTasks) {
      setTasks(JSON.parse(savedTasks));
    }

    if (savedCompletedTasks) {
      setCompletedTasks(JSON.parse(savedCompletedTasks));
    }
  }, []);

  // 保存任务列表和已完成任务到本地存储
  useEffect(() => {
    localStorage.setItem('tasks', JSON.stringify(tasks));
  }, [tasks]);

  useEffect(() => {
    localStorage.setItem('completedTasks', JSON.stringify(completedTasks));
  }, [completedTasks]);

  // 添加任务
  const handleAddTask = () => {
    if (inputValue.trim() !== '') {
      const newTask = {
        id: Date.now(),
        text: inputValue.trim(),
        timestamp: new Date().toISOString()
      };
      setTasks([...tasks, newTask]);
      setInputValue('');
    }
  };

  // 处理输入框回车事件
  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleAddTask();
    }
  };

  // 完成任务
  const handleCompleteTask = (id) => {
    const taskToComplete = tasks.find(task => task.id === id);
    if (taskToComplete) {
      setCompletedTasks([...completedTasks, {
        ...taskToComplete,
        completedAt: new Date().toISOString()
      }]);
      setTasks(tasks.filter(task => task.id !== id));
    }
  };

  // 删除任务
  const handleDeleteTask = (id) => {
    setTasks(tasks.filter(task => task.id !== id));
  };

  // 从历史记录中恢复任务
  const handleRestoreTask = (id) => {
    const taskToRestore = completedTasks.find(task => task.id === id);
    if (taskToRestore) {
      setTasks([...tasks, {
        id: Date.now(), // 生成新的ID以避免冲突
        text: taskToRestore.text,
        timestamp: new Date().toISOString()
      }]);
      setCompletedTasks(completedTasks.filter(task => task.id !== id));
    }
  };

  // 登录处理
  const handleLogin = (e) => {
    e.preventDefault();

    // 简单的登录验证逻辑（实际项目中应该连接到后端API）
    if (email && password) {
      setIsLoggedIn(true);
      setIsGuest(false);
      setShowLoginModal(false);
      setEmail('');
      setPassword('');

      // 加载用户数据
      loadUserData(email);
    }
  };

  // 游客登录
  const handleGuestLogin = () => {
    setIsLoggedIn(true);
    setIsGuest(true);
    setShowLoginModal(false);
  };

  // 注销
  const handleLogout = () => {
    setIsLoggedIn(false);
    setIsGuest(false);
    setEmail('');
    setPassword('');

    // 清空用户数据
    setTasks([]);
    setCompletedTasks([]);
    setJokes([]);
    setCurrentPage(1);
  };

  // 加载用户数据（模拟）
  const loadUserData = (userEmail) => {
    // 这里模拟从API加载用户数据
    console.log(`加载用户 ${userEmail} 的数据`);
    // 实际项目中，这里应该是API请求
  };

  // 生成随机笑话
  const generateRandomJokes = () => {
    const jokeTemplates = [
      "为什么程序员总是分不清万圣节和圣诞节？因为 Oct 31 = Dec 25",
      "为什么程序员喜欢爬山？因为他们想要更高的堆栈",
      "为什么程序员不喜欢大自然？因为有太多的bugs",
      "程序员的妻子让他去买六个鸡蛋，如果看到西瓜就买一个。他买了一个鸡蛋回来，因为他看到了西瓜",
      "什么是程序员最好的朋友？咖啡和代码注释",
      "为什么程序员总是随身携带雨伞？因为他们害怕程序下雨（downpour）",
      "程序员如何安慰哭泣的孩子？别哭了，这只是一个bug，我们可以修复它",
      "为什么程序员总是单身？因为他们不明白什么是'关系'（relationship）",
      "程序员的新年决心是什么？减少代码中的bug",
      "为什么程序员喜欢黑暗模式？因为光会暴露他们的代码缺陷",
      "程序员最讨厌的三个问题：这需要多长时间？能做吗？为什么会这样？",
      "程序员如何喝咖啡？他们先调试（吹一吹），然后执行（喝）",
      "为什么程序员总是在电脑前吃饭？因为他们不想中断他们的进程",
      "程序员如何称呼他们的宠物？变量名，比如：Fido_1, Fido_2",
      "为什么程序员不喜欢开玩笑？因为他们总是认真（literal）地理解",
      "程序员的情人节礼物是什么？一个新的键盘和无限的代码时间",
      "为什么程序员喜欢冬天？因为他们可以穿着连帽衫（hoodie）编程",
      "程序员如何庆祝生日？他们增加自己的版本号",
      "为什么程序员总是迟到？因为他们的时间复杂度是O(1)，但实际运行时间是O(n²)",
      "程序员最常用的口头禅是什么？这在我的机器上是正常工作的",
      "为什么程序员总是戴着眼镜？因为他们需要看到代码的细节",
      "程序员如何选择职业？他们跟着他们的直觉（intellisense）走",
      "为什么程序员不喜欢社交聚会？因为他们不擅长处理实时交互",
      "程序员的周末计划是什么？修复上周的代码和编写下周的bug",
      "为什么程序员总是在打字？因为他们有很多想法要输出（print）",
      "程序员如何看待休息？这只是一个短暂的暂停（pause），然后继续执行（resume）",
      "为什么程序员喜欢算法？因为它们有明确的输入、输出和步骤",
      "程序员最讨厌的事情是什么？当他们的代码在没有任何修改的情况下突然不工作了",
      "程序员如何学习新事物？他们先看文档，然后尝试，最后调试",
      "为什么程序员总是在思考？因为他们的大脑就像一个运行中的程序",
      "程序员的理想约会是什么样的？两个人一起编码，互相debug对方的代码",
      "为什么程序员不喜欢变化？因为变化意味着更多的测试和调试",
      "程序员如何应对压力？他们深呼吸，然后开始写代码",
      "为什么程序员喜欢数学？因为数学有明确的答案，不像编程",
      "程序员最自豪的时刻是什么？当他们的代码第一次运行没有bug的时候",
      "为什么程序员总是使用缩写？因为他们喜欢节省时间和空间",
      "程序员如何定义成功？当他们的代码不仅能工作，而且看起来很优雅",
      "为什么程序员不喜欢写文档？因为代码就是最好的文档",
      "程序员如何度过假期？他们仍然在思考新的项目和功能",
      "为什么程序员总是在更新他们的软件？因为新版本总是更好的（或者至少他们是这么认为的）",
      "程序员的恐惧是什么？失去他们的代码，或者忘记保存",
      "为什么程序员喜欢开源？因为分享是关心的表现",
      "程序员如何看待错误？错误只是学习的机会",
      "为什么程序员总是在喝咖啡？因为咖啡因是他们的燃料",
      "程序员的梦想是什么？写出完美的代码，永远不需要调试",
      "为什么程序员不喜欢会议？因为会议占用了他们的编码时间",
      "程序员如何放松？他们玩电子游戏，或者看其他人编码",
      "为什么程序员总是在熬夜？因为夜晚是编码的最佳时间，没有打扰",
      "程序员的秘密是什么？他们其实并不知道所有的答案，但他们知道如何找到它们"
    ];

    // 生成100条唯一的笑话
    const uniqueJokes = [];
    while (uniqueJokes.length < 100) {
      const randomIndex = Math.floor(Math.random() * jokeTemplates.length);
      const jokeWithId = {
        id: `joke-${Date.now()}-${Math.floor(Math.random() * 1000)}`,
        content: jokeTemplates[randomIndex],
        date: new Date().toISOString()
      };

      // 确保笑话是唯一的
      if (!uniqueJokes.some(joke => joke.content === jokeWithId.content)) {
        uniqueJokes.push(jokeWithId);
      }
    }

    setJokes(uniqueJokes);
  };

  // 分页逻辑
  const indexOfLastJoke = currentPage * jokesPerPage;
  const indexOfFirstJoke = indexOfLastJoke - jokesPerPage;
  const currentJokes = jokes.slice(indexOfFirstJoke, indexOfLastJoke);
  const totalPages = Math.ceil(jokes.length / jokesPerPage);

  // 页面加载时生成笑话
  useEffect(() => {
    generateRandomJokes();
  }, []);

  // 切换页面
  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  // 移动端菜单状态
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);

  return (
    <div className="main-layout">
      {/* 移动端汉堡菜单按钮 */}
      <button 
        className="mobile-menu-btn"
        onClick={() => setMobileMenuOpen(true)}
      >
        ☰
      </button>

      {/* 侧边栏 */}
      <div className={`sidebar ${mobileMenuOpen ? 'show' : ''}`}>
        <button 
          className="mobile-close-btn"
          onClick={() => setMobileMenuOpen(false)}
        >
          ×
        </button>
        <div className="logo">TODO LIST</div>
        <nav className="sidebar-nav">
          <button 
            className={`sidebar-btn ${activePage === 'jokes' ? 'active' : ''}`}
            onClick={() => {
              setActivePage('jokes');
              setMobileMenuOpen(false);
            }}
          >
            😄 笑话管理
          </button>
          <button 
            className={`sidebar-btn ${activePage === 'tasks' ? 'active' : ''}`}
            onClick={() => {
              setActivePage('tasks');
              setMobileMenuOpen(false);
            }}
          >
            📝 任务列表
          </button>
          <button 
            className={`sidebar-btn ${activePage === 'analytics' ? 'active' : ''}`}
            onClick={() => {
              setActivePage('analytics');
              setMobileMenuOpen(false);
            }}
          >
            📊 数据分析
          </button>
          <button 
            className={`sidebar-btn ${activePage === 'games' ? 'active' : ''}`}
            onClick={() => {
              setActivePage('games');
              setMobileMenuOpen(false);
            }}
          >
            🎮 游戏中心
          </button>
          <button 
            className={`sidebar-btn ${activePage === 'travel' ? 'active' : ''}`}
            onClick={() => {
              setActivePage('travel');
              setMobileMenuOpen(false);
            }}
          >
            ✈️ 旅游计划
          </button>
        </nav>
      </div>

      {/* 顶部导航栏 - 优化样式 */}
      <div className="nav-buttons-with-user">
        <div className="nav-buttons">
          <button 
            className={`nav-btn ${activePage === 'jokes' ? 'active' : ''}`}
            onClick={() => setActivePage('jokes')}
          >
            😄 笑话
          </button>
          <button 
            className={`nav-btn ${activePage === 'tasks' ? 'active' : ''}`}
            onClick={() => setActivePage('tasks')}
          >
            📝 任务
          </button>
          <button 
            className={`nav-btn ${activePage === 'analytics' ? 'active' : ''}`}
            onClick={() => setActivePage('analytics')}
          >
            📊 分析
          </button>
          <button 
            className={`nav-btn ${activePage === 'games' ? 'active' : ''}`}
            onClick={() => setActivePage('games')}
          >
            🎮 游戏
          </button>
          <button 
            className={`nav-btn ${activePage === 'travel' ? 'active' : ''}`}
            onClick={() => setActivePage('travel')}
          >
            ✈️ 旅游
          </button>
        </div>

        <div className="user-info">
          {isLoggedIn ? (
            <div className="user-info-details">
              <span className="user-email">
                {isGuest ? '👤 游客' : `👤 ${email}`}
              </span>
              <button className="logout-btn" onClick={handleLogout}>
                注销
              </button>
            </div>
          ) : (
            <button 
              className="login-btn"
              onClick={() => setShowLoginModal(true)}
            >
              登录
            </button>
          )}
        </div>
      </div>

      {/* 主内容区域 */}
      <main className="content">
        {/* 旅游计划页面 */}
        {activePage === 'travel' && <TravelPlan />}

        {/* 任务列表页面 */}
        {activePage === 'tasks' && (
          <div className="todo-container">
            <h2>📝 任务列表</h2>
            <div className="input-container">
              <input
                type="text"
                value={inputValue}
                onChange={(e) => setInputValue(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder="添加新任务..."
              />
              <button onClick={handleAddTask}>添加</button>
            </div>

            <ul className="task-list">
              {tasks.length === 0 ? (
                <li className="empty-state">暂无任务，添加一个吧！</li>
              ) : (
                tasks.map(task => (
                  <li key={task.id}>
                    <span className="task-text">{task.text}</span>
                    <div className="task-buttons">
                      <button onClick={() => handleCompleteTask(task.id)}>完成</button>
                      <button className="delete-btn" onClick={() => handleDeleteTask(task.id)}>删除</button>
                    </div>
                  </li>
                ))
              )}
            </ul>

            {completedTasks.length > 0 && (
              <div className="history-section">
                <button 
                  className="history-toggle"
                  onClick={() => setShowHistory(!showHistory)}
                >
                  {showHistory ? '隐藏' : '查看'}已完成任务 ({completedTasks.length})
                </button>

                {showHistory && (
                  <ul className="history-list">
                    {completedTasks.map(task => (
                      <li key={task.id} className="history-item">
                        <div className="history-info">
                          <span className="history-text">{task.text}</span>
                          <span className="history-time">
                            完成于 {new Date(task.completedAt).toLocaleString()}
                          </span>
                        </div>
                        <div className="history-actions">
                          <button 
                            className="restore-btn"
                            onClick={() => handleRestoreTask(task.id)}
                          >
                            恢复
                          </button>
                        </div>
                      </li>
                    ))}
                  </ul>
                )}
              </div>
            )}
          </div>
        )}

        {/* 数据分析页面 */}
        {activePage === 'analytics' && (
          <div className="analytics-container">
            <h2>📊 数据分析</h2>

            <div className="stats-grid">
              <div className="stat-card">
                <h3>总任务数</h3>
                <p className="stat-number">{tasks.length + completedTasks.length}</p>
              </div>
              <div className="stat-card">
                <h3>已完成任务</h3>
                <p className="stat-number">{completedTasks.length}</p>
              </div>
            </div>

            <div className="progress-section">
              <h3>完成进度</h3>
              <div className="progress-bar">
                <div 
                  className="progress-fill"
                  style={{
                    width: `${tasks.length + completedTasks.length > 0 
                      ? (completedTasks.length / (tasks.length + completedTasks.length)) * 100 
                      : 0}%`
                  }}
                ></div>
              </div>
              <p className="progress-text">
                {tasks.length + completedTasks.length > 0 
                  ? `${Math.round((completedTasks.length / (tasks.length + completedTasks.length)) * 100)}%`
                  : '0%'}
              </p>
            </div>

            <div className="chart-placeholder">
              <h3>任务趋势</h3>
              <div className="bar-chart">
                {/* 这里应该是图表，但我们使用占位符 */}
                {[1, 2, 3, 4, 5, 6, 7].map(day => (
                  <div key={day} className="chart-bar">
                    <div 
                      className="bar"
                      style={{ height: `${Math.random() * 100 + 50}px` }}
                    ></div>
                    <span>第{day}天</span>
                  </div>
                ))}
              </div>
            </div>
          </div>
        )}

        {/* 游戏管理页面 */}
        {activePage === 'games' && (
          <div className="games-container">
            <h2>🎮 游戏中心</h2>
            <div className="game-selection">
              <button className="game-btn">猜数字</button>
              <button className="game-btn">2048</button>
              <button className="game-btn">贪吃蛇</button>
              <button className="game-btn">俄罗斯方块</button>
            </div>
            <div className="game-wrapper">
              <p>请选择一个游戏开始玩吧！</p>
            </div>
          </div>
        )}

        {/* 笑话管理页面 */}
        {activePage === 'jokes' && (
          <div className="jokes-container">
            <h2>😄 笑话管理</h2>
            <p className="jokes-count">共 {jokes.length} 条笑话</p>

            <div className="jokes-list">
              {currentJokes.map(joke => (
                <div key={joke.id} className="joke-item">
                  <p className="joke-content">{joke.content}</p>
                  <div className="joke-meta">
                    <span className="joke-id">#{joke.id}</span>
                    <span className="joke-date">
                      {new Date(joke.date).toLocaleDateString()}
                    </span>
                  </div>
                </div>
              ))}
            </div>

            {/* 分页控件 */}
            {totalPages > 1 && (
              <div className="pagination">
                <button 
                  className="page-btn"
                  onClick={() => handlePageChange(currentPage - 1)}
                  disabled={currentPage === 1}
                >
                  上一页
                </button>

                <div className="page-numbers">
                  {currentPage > 3 && (
                    <>
                      <button 
                        className="page-btn"
                        onClick={() => handlePageChange(1)}
                      >
                        1
                      </button>
                      {currentPage > 4 && <span className="page-ellipsis">...</span>}
                    </>
                  )}

                  {[...Array(totalPages)].map((_, index) => {
                    const pageNum = index + 1;
                    if (pageNum >= currentPage - 2 && pageNum <= currentPage + 2) {
                      return (
                        <button 
                          key={pageNum}
                          className={`page-btn ${currentPage === pageNum ? 'active' : ''}`}
                          onClick={() => handlePageChange(pageNum)}
                        >
                          {pageNum}
                        </button>
                      );
                    }
                    return null;
                  })}

                  {currentPage < totalPages - 2 && (
                    <>
                      {currentPage < totalPages - 3 && <span className="page-ellipsis">...</span>}
                      <button 
                        className="page-btn"
                        onClick={() => handlePageChange(totalPages)}
                      >
                        {totalPages}
                      </button>
                    </>
                  )}
                </div>

                <button 
                  className="page-btn"
                  onClick={() => handlePageChange(currentPage + 1)}
                  disabled={currentPage === totalPages}
                >
                  下一页
                </button>
              </div>
            )}
          </div>
        )}
      </main>

      {/* 登录弹窗 - 优化样式 */}
      {showLoginModal && (
        <div className="login-modal-overlay">
          <div className="login-modal">
            <div className="login-modal-header">
              <h2>👤 用户登录</h2>
              <button 
                className="close-modal-btn"
                onClick={() => setShowLoginModal(false)}
              >
                ×
              </button>
            </div>

            <form onSubmit={handleLogin} className="login-form">
              <div className="form-group">
                <label htmlFor="email">邮箱</label>
                <input
                  type="email"
                  id="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  placeholder="请输入邮箱"
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="password">密码</label>
                <input
                  type="password"
                  id="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  placeholder="请输入密码"
                  required
                />
              </div>

              <button type="submit" className="login-submit-btn">
                登录
              </button>
            </form>

            <div className="login-divider">
              <span>或</span>
            </div>

            <button 
              className="guest-login-btn"
              onClick={handleGuestLogin}
            >
              👥 游客登录
            </button>

            <div className="login-demo-info">
              <p className="demo-text">演示账号：</p>
              <p>email: demo@example.com</p>
              <p>password: 123456</p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
