import React, { useState, useEffect } from 'react';
import './RealEstateInfo.css';

function RealEstateComparison() {
  const [isVisible, setIsVisible] = useState({});
  const [sortConfig, setSortConfig] = useState({ key: null, direction: 'ascending' });
  const [selectedAreas, setSelectedAreas] = useState(['all']);
  const [viewMode, setViewMode] = useState('table'); // 'table' or 'card'

  // ÁõëÂê¨ÊªöÂä®ÊòæÁ§∫Âä®Áîª
  useEffect(() => {
    const handleScroll = () => {
      const sections = document.querySelectorAll('.section-animated');
      const newVisibility = {};

      sections.forEach(section => {
        const rect = section.getBoundingClientRect();
        const id = section.getAttribute('data-section');
        if (rect.top < window.innerHeight * 0.75 && rect.bottom > 0) {
          newVisibility[id] = true;
        }
      });

      setIsVisible(prev => ({ ...prev, ...newVisibility }));
    };

    window.addEventListener('scroll', handleScroll);
    handleScroll(); // ÂàùÂßãÊ£ÄÊü•

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  // Ë°®Ê†ºÊéíÂ∫èÂäüËÉΩ
  const handleSort = (key) => {
    let direction = 'ascending';
    if (sortConfig.key === key && sortConfig.direction === 'ascending') {
      direction = 'descending';
    }
    setSortConfig({ key, direction });
  };

  // Âå∫ÂüüÈÄâÊã©ÂäüËÉΩ
  const handleAreaToggle = (area) => {
    if (area === 'all') {
      setSelectedAreas(['all']);
    } else {
      setSelectedAreas(prev => {
        // Â¶ÇÊûúÂ∑≤ÁªèÈÄâÊã©‰∫Ü'all'ÔºåÂÖàÁßªÈô§
        if (prev.includes('all')) {
          return [area];
        }
        // ÂàáÊç¢ÈÄâ‰∏≠Áä∂ÊÄÅ
        if (prev.includes(area)) {
          const newAreas = prev.filter(a => a !== area);
          // Â¶ÇÊûúÊ≤°ÊúâÈÄâ‰∏≠‰ªª‰ΩïÂå∫ÂüüÔºåÈªòËÆ§ÈÄâ‰∏≠'all'
          return newAreas.length === 0 ? ['all'] : newAreas;
        } else {
          return [...prev, area];
        }
      });
    }
  };

  // ËßÜÂõæÊ®°ÂºèÂàáÊç¢
  const handleViewModeToggle = (mode) => {
    setViewMode(mode);
  };

  // Âå∫Âüü‰ª∑Ê†ºÊï∞ÊçÆÔºàÁî®‰∫éÂõæË°®Ôºâ
  const areaPriceData = [
    { area: 'ÂçóÂ±±Âå∫', newPrice: 12.6, resalePrice: 12.0 },
    { area: 'Á¶èÁî∞Âå∫', newPrice: 10.6, resalePrice: 10.0 },
    { area: 'ÁΩóÊπñÂå∫', newPrice: 7.8, resalePrice: 7.2 },
    { area: 'ÈæôÂçéÂå∫', newPrice: 6.9, resalePrice: 6.3 },
    { area: 'ÂÆùÂÆâÂå∫', newPrice: 7.6, resalePrice: 7.1 },
    { area: 'ÈæôÂ≤óÂå∫', newPrice: 5.5, resalePrice: 5.1 },
    { area: 'ÂÖâÊòéÂå∫', newPrice: 5.3, resalePrice: 4.9 },
    { area: 'Âù™Â±±Âå∫', newPrice: 4.5, resalePrice: 4.2 }
  ];

  return (
    <div className="real-estate-container">
      {/* ËøîÂõû‰∏ªÈ°µÈù¢ÈìæÊé• */}
      <div className="back-link">
        <a href="#" onClick={(e) => {
          e.preventDefault();
          window.location.hash = '#info';
          const event = new CustomEvent('navigateToPage', { detail: { page: 'realestateInfo' } });
          window.dispatchEvent(event);
        }}>
          ‚Üê ËøîÂõûÊ∑±Âú≥ÈæôÂçéÁ∫¢Â±±ÁâáÂå∫Êàø‰∫ß‰ø°ÊÅØ
        </a>
      </div>

      <h2>üèòÔ∏è Ê∑±Âú≥ÂêÑÂå∫ÂüüÊàø‰∫ßÂØπÊØîÂàÜÊûê (2025Âπ¥)</h2>

      <p className="intro-text">
        Ê∑±Âú≥‰∏çÂêåÂå∫ÂüüÁöÑÊàø‰∫ßÂ∏ÇÂú∫ÂêÑÂÖ∑ÁâπËâ≤Ôºå‰ª∑Ê†ºÂ∑ÆÂºÇËæÉÂ§ß„ÄÇ‰ª•‰∏ãÊòØ2025Âπ¥Ê∑±Âú≥‰∏ªË¶ÅÂå∫ÂüüÁöÑÊàø‰ª∑ÂØπÊØî„ÄÅÂå∫ÂüüÁâπÁÇπÂèäË¥≠ÊàøÂª∫ËÆÆÔºåÂ∏åÊúõËÉΩÂ∏ÆÂä©ÊÇ®ÊâæÂà∞ÈÄÇÂêàËá™Â∑±ÁöÑÂå∫Âüü„ÄÇ
      </p>

      {/* Âå∫Âüü‰ª∑Ê†ºÂØπÊØîÂõæË°® */}
      <div className="chart-section section-animated" data-section="chart">
        <h3>üìä Ê∑±Âú≥ÂêÑÂå∫ÂüüÊàø‰ª∑ÂØπÊØî</h3>
        <div className="chart-wrapper">
          <svg viewBox="0 0 800 400" className="area-chart">
            {/* XËΩ¥ */}
            <line x1="80" y1="350" x2="750" y2="350" stroke="#cbd5e1" strokeWidth="2" />
            {/* YËΩ¥ */}
            <line x1="80" y1="50" x2="80" y2="350" stroke="#cbd5e1" strokeWidth="2" />

            {/* YËΩ¥ÂàªÂ∫¶ */}
            {[4, 6, 8, 10, 12, 14].map((value, i) => (
              <g key={i}>
                <line x1="78" y1={350 - (value - 4) * 30} x2="82" y2={350 - (value - 4) * 30} stroke="#cbd5e1" strokeWidth="1" />
                <text x="60" y={350 - (value - 4) * 30 + 5} textAnchor="middle" fill="#64748b" fontSize="12">{value}</text>
              </g>
            ))}

            {/* Êü±Áä∂Âõæ - Êñ∞Êàø‰ª∑Ê†º */}
            {areaPriceData.map((item, index) => {
              const x = 130 + index * 80;
              const height = (item.newPrice - 4) * 30;
              return (
                <g key={`new-${index}`}>
                  <rect x={x - 15} y={350 - height} width="20" height={height} fill="#4f46e5" rx="4" />
                  <text x={x} y={350 - height - 10} textAnchor="middle" fill="#4f46e5" fontWeight="600" fontSize="12">{item.newPrice}</text>
                </g>
              );
            })}

            {/* Êü±Áä∂Âõæ - ‰∫åÊâãÊàø‰ª∑Ê†º */}
            {areaPriceData.map((item, index) => {
              const x = 130 + index * 80 + 25;
              const height = (item.resalePrice - 4) * 30;
              return (
                <g key={`resale-${index}`}>
                  <rect x={x - 15} y={350 - height} width="20" height={height} fill="#0ea5e9" rx="4" />
                  <text x={x} y={350 - height - 10} textAnchor="middle" fill="#0ea5e9" fontWeight="600" fontSize="12">{item.resalePrice}</text>
                  <text x={130 + index * 80 + 12.5} y="370" textAnchor="middle" fill="#64748b" fontSize="12">{item.area}</text>
                </g>
              );
            })}

            {/* Âõæ‰æã */}
            <text x="425" y="30" textAnchor="middle" fill="#475569" fontSize="14" fontWeight="600">Âùá‰ª∑Ôºà‰∏á/„é°Ôºâ</text>
            <g>
              <rect x="350" y="45" width="15" height="10" fill="#4f46e5" rx="2" />
              <text x="375" y="55" fill="#64748b" fontSize="12">Êñ∞Êàø</text>
              <rect x="420" y="45" width="15" height="10" fill="#0ea5e9" rx="2" />
              <text x="445" y="55" fill="#64748b" fontSize="12">‰∫åÊâãÊàø</text>
            </g>
          </svg>
        </div>
      </div>

      {/* Âå∫ÂüüÈÄâÊã©Âô® */}
      <div className="area-selector section-animated" data-section="selector">
        <h3>üìç Âå∫ÂüüÈÄâÊã©</h3>
        <div className="area-tags">
          <button 
            className={`area-tag ${selectedAreas.includes('all') ? 'active' : ''}`}
            onClick={() => handleAreaToggle('all')}
          >
            ÂÖ®ÈÉ®Âå∫Âüü
          </button>
          <button 
            className={`area-tag ${selectedAreas.includes('ÂçóÂ±±Âå∫') ? 'active' : ''}`}
            onClick={() => handleAreaToggle('ÂçóÂ±±Âå∫')}
          >
            ÂçóÂ±±Âå∫
          </button>
          <button 
            className={`area-tag ${selectedAreas.includes('Á¶èÁî∞Âå∫') ? 'active' : ''}`}
            onClick={() => handleAreaToggle('Á¶èÁî∞Âå∫')}
          >
            Á¶èÁî∞Âå∫
          </button>
          <button 
            className={`area-tag ${selectedAreas.includes('ÁΩóÊπñÂå∫') ? 'active' : ''}`}
            onClick={() => handleAreaToggle('ÁΩóÊπñÂå∫')}
          >
            ÁΩóÊπñÂå∫
          </button>
          <button 
            className={`area-tag ${selectedAreas.includes('ÈæôÂçéÂå∫') ? 'active' : ''}`}
            onClick={() => handleAreaToggle('ÈæôÂçéÂå∫')}
          >
            ÈæôÂçéÂå∫
          </button>
          <button 
            className={`area-tag ${selectedAreas.includes('ÂÆùÂÆâÂå∫') ? 'active' : ''}`}
            onClick={() => handleAreaToggle('ÂÆùÂÆâÂå∫')}
          >
            ÂÆùÂÆâÂå∫
          </button>
          <button 
            className={`area-tag ${selectedAreas.includes('ÈæôÂ≤óÂå∫') ? 'active' : ''}`}
            onClick={() => handleAreaToggle('ÈæôÂ≤óÂå∫')}
          >
            ÈæôÂ≤óÂå∫
          </button>
          <button 
            className={`area-tag ${selectedAreas.includes('ÂÖâÊòéÂå∫') ? 'active' : ''}`}
            onClick={() => handleAreaToggle('ÂÖâÊòéÂå∫')}
          >
            ÂÖâÊòéÂå∫
          </button>
          <button 
            className={`area-tag ${selectedAreas.includes('Âù™Â±±Âå∫') ? 'active' : ''}`}
            onClick={() => handleAreaToggle('Âù™Â±±Âå∫')}
          >
            Âù™Â±±Âå∫
          </button>
        </div>
      </div>

      {/* ËßÜÂõæÊ®°ÂºèÂàáÊç¢ */}
      <div className="view-mode-switcher">
        <button 
          className={`view-mode-btn ${viewMode === 'table' ? 'active' : ''}`}
          onClick={() => handleViewModeToggle('table')}
        >
          üìã Ë°®Ê†ºËßÜÂõæ
        </button>
        <button 
          className={`view-mode-btn ${viewMode === 'card' ? 'active' : ''}`}
          onClick={() => handleViewModeToggle('card')}
        >
          üì± Âç°ÁâáËßÜÂõæ
        </button>
      </div>

      {/* Ë°®Ê†ºËßÜÂõæ */}
      <div className={`table-section section-animated ${viewMode === 'table' ? 'active' : 'hidden'}`} data-section="table">
        <h3>üìã ÂêÑÂå∫ÂüüÊàø‰∫ßËØ¶ÁªÜÂØπÊØî</h3>
        <div className="table-container">
          <table className="estate-table">
            <thead>
              <tr>
                <th onClick={() => handleSort('area')} className="sortable">
                  Âå∫Âüü {sortConfig.key === 'area' && (sortConfig.direction === 'ascending' ? '‚Üë' : '‚Üì')}
                </th>
                <th onClick={() => handleSort('newPrice')} className="sortable">
                  Êñ∞ÊàøÂùá‰ª∑Ôºà‰∏á/„é°Ôºâ {sortConfig.key === 'newPrice' && (sortConfig.direction === 'ascending' ? '‚Üë' : '‚Üì')}
                </th>
                <th onClick={() => handleSort('resalePrice')} className="sortable">
                  ‰∫åÊâãÊàøÂùá‰ª∑Ôºà‰∏á/„é°Ôºâ {sortConfig.key === 'resalePrice' && (sortConfig.direction === 'ascending' ? '‚Üë' : '‚Üì')}
                </th>
                <th>‰ª£Ë°®Ê•ºÁõò</th>
                <th>Âå∫ÂüüÁâπÁÇπ</th>
                <th>ÈÄÇÂêà‰∫∫Áæ§</th>
              </tr>
            </thead>
            <tbody>
              {(() => {
                // ÂéüÂßãÊï∞ÊçÆ
                const tableData = [
                  { area: 'ÂçóÂ±±Âå∫', newPrice: '9.8-15.5', resalePrice: '9.2-14.8', projects: 'Ê∑±Âú≥Êπæ1Âè∑„ÄÅÂçéÊ∂¶ÂüéÊ∂¶Â∫ú', features: '<span className="feature">ÁßëÊäÄ‰∫ß‰∏ö</span>ÈõÜ‰∏≠ÔºåÊïôËÇ≤ËµÑÊ∫ê‰ºòË¥®ÔºåÈÖçÂ•óÂÆåÂñÑ', suitableFor: 'È´òÊî∂ÂÖ•‰∫∫Áæ§„ÄÅÁßëÊäÄ‰ªé‰∏öËÄÖ„ÄÅÈáçËßÜÊïôËÇ≤ÁöÑÂÆ∂Â∫≠' },
                  { area: 'Á¶èÁî∞Âå∫', newPrice: '8.5-12.8', resalePrice: '7.9-12.2', projects: 'È¶ôËúúÊπñ‰∏ÄÂè∑„ÄÅÈáëËåÇÂ∫ú', features: 'Â∏Ç‰∏≠ÂøÉÊ†∏ÂøÉÂå∫ÂüüÔºåÂïÜ‰∏öÁπÅÂçéÔºå‰∫§ÈÄö‰æøÂà©', suitableFor: 'ÈáëËûç‰ªé‰∏öËÄÖ„ÄÅÂïÜÂä°‰∫∫Â£´„ÄÅÂñúÊ¨¢ÂüéÂ∏Ç‰∏≠ÂøÉÁîüÊ¥ªÁöÑÂÆ∂Â∫≠' },
                  { area: 'ÁΩóÊπñÂå∫', newPrice: '6.5-9.2', resalePrice: '5.8-8.7', projects: 'ÂçéÊ∂¶ÁΩÆÂú∞‰∏áË±°Â∫ú„ÄÅÊãõÂïÜ‰∏≠ÁéØ', features: 'ËÄÅÂüéÂå∫ÔºåÁîüÊ¥ªÈÖçÂ•óÊàêÁÜüÔºå‰∫§ÈÄö‰æøÊç∑', suitableFor: 'È¢ÑÁÆóÊúâÈôê‰ΩÜÊ≥®ÈáçÁîüÊ¥ª‰æøÂà©ÊÄßÁöÑË¥≠ÊàøËÄÖ' },
                  { area: 'ÈæôÂçéÂå∫', newPrice: '5.5-8.3', resalePrice: '4.9-7.8', projects: 'Á∫¢Â±±Â∫ú„ÄÅÂ∞öË™âÁ∫¢Â±±Èáå', features: 'ÂåóÈÉ®ÂèëÂ±ïÊñ∞Âå∫Ôºå‰∫§ÈÄöÊó•ÁõäÂÆåÂñÑÔºåÊïôËÇ≤ËµÑÊ∫êÊèêÂçá', suitableFor: 'ÂàöÈúÄË¥≠ÊàøËÄÖ„ÄÅÂÖ≥Ê≥®ÊÄß‰ª∑ÊØîÁöÑÂÆ∂Â∫≠' },
                  { area: 'ÂÆùÂÆâÂå∫', newPrice: '5.8-9.5', resalePrice: '5.2-9.0', projects: 'Â£πÊñπ‰∏≠ÂøÉ„ÄÅ‰∏≠Ê¥≤Êπæ', features: 'Ë•øÈÉ®ÂèëÂ±ïÈáçÁÇπÂå∫ÂüüÔºå‰∏¥ÂâçÊµ∑Ôºå‰∫§ÈÄö‰æøÂà©', suitableFor: 'ÊîπÂñÑÂûãÈúÄÊ±Ç„ÄÅÂÖ≥Ê≥®Âå∫ÂüüÂèëÂ±ïÊΩúÂäõÁöÑË¥≠ÊàøËÄÖ' },
                  { area: 'ÈæôÂ≤óÂå∫', newPrice: '4.2-6.8', resalePrice: '3.8-6.5', projects: '‰ø°‰πâÈáëÂæ°ÂçäÂ±±„ÄÅ‰∏áÁßëÈ∫ìÂüé', features: '‰∏úÈÉ®ÂèëÂ±ïÂå∫ÂüüÔºåÊàø‰ª∑Áõ∏ÂØπËæÉ‰ΩéÔºåÁîüÊÄÅÁéØÂ¢ÉÂ•Ω', suitableFor: 'ÂàöÈúÄË¥≠ÊàøËÄÖ„ÄÅÈ¢ÑÁÆóÊúâÈôêÁöÑÂπ¥ËΩªÂÆ∂Â∫≠' },
                  { area: 'ÂÖâÊòéÂå∫', newPrice: '4.5-6.2', resalePrice: '4.0-5.8', projects: 'ÈæôÂÖâÁéñÈæôÂè∞„ÄÅÂã§ËØöËææÊ≠£Â§ßÂüé', features: 'Êñ∞ÂÖ¥‰∫ß‰∏öÂõ≠Âå∫ÔºåÊàø‰ª∑Áõ∏ÂØπËæÉ‰ΩéÔºåÂèëÂ±ïÊΩúÂäõÂ§ß', suitableFor: 'ÈïøÊúüÊäïËµÑËÄÖ„ÄÅÈ¢ÑÁÆóÊúâÈôê‰ΩÜÁúãÂ•ΩÂå∫ÂüüÂèëÂ±ïÁöÑË¥≠ÊàøËÄÖ' },
                  { area: 'Âù™Â±±Âå∫', newPrice: '3.8-5.2', resalePrice: '3.5-4.9', projects: 'Ê∑±ÂüéÊäï‰∏≠ÂøÉÂÖ¨È¶Ü„ÄÅÈæôÂÖâÁéñ‰∫ëËëó', features: '‰∏úÈÉ®Êñ∞ÂÖ¥Âå∫ÂüüÔºåÊàø‰ª∑ÊúÄ‰ΩéÔºåËßÑÂàíÂèëÂ±ï‰∏≠', suitableFor: 'ÊûÅÂàöÈúÄË¥≠ÊàøËÄÖ„ÄÅÈïøÊúüÊäïËµÑËÄÖ' }
                ];

                // ËøáÊª§Êï∞ÊçÆ
                let filteredData = tableData;
                if (!selectedAreas.includes('all')) {
                  filteredData = tableData.filter(item => selectedAreas.includes(item.area));
                }

                // ÊéíÂ∫èÊï∞ÊçÆ
                if (sortConfig.key !== null) {
                  filteredData.sort((a, b) => {
                    // Â§ÑÁêÜ‰ª∑Ê†ºËåÉÂõ¥ÁöÑÊéíÂ∫è
                    if (sortConfig.key === 'newPrice' || sortConfig.key === 'resalePrice') {
                      const aPrice = parseFloat(a[sortConfig.key].split('-')[0]);
                      const bPrice = parseFloat(b[sortConfig.key].split('-')[0]);
                      if (aPrice < bPrice) {
                        return sortConfig.direction === 'ascending' ? -1 : 1;
                      }
                      if (aPrice > bPrice) {
                        return sortConfig.direction === 'ascending' ? 1 : -1;
                      }
                    } else {
                      if (a[sortConfig.key] < b[sortConfig.key]) {
                        return sortConfig.direction === 'ascending' ? -1 : 1;
                      }
                      if (a[sortConfig.key] > b[sortConfig.key]) {
                        return sortConfig.direction === 'ascending' ? 1 : -1;
                      }
                    }
                    return 0;
                  });
                }

                // Ê∏≤ÊüìÊï∞ÊçÆ
                return filteredData.map((item, index) => (
                  <tr key={index}>
                    <td className="highlight">{item.area}</td>
                    <td>{item.newPrice}</td>
                    <td>{item.resalePrice}</td>
                    <td>{item.projects}</td>
                    <td dangerouslySetInnerHTML={{ __html: item.features }} />
                    <td>{item.suitableFor}</td>
                  </tr>
                ));
              })()}
            </tbody>
          </table>
        </div>
      </div>

      {/* Âå∫ÂüüÂç°ÁâáËßÜÂõæ */}
      <div className={`area-cards-container section-animated ${viewMode === 'card' ? 'active' : 'hidden'}`} data-section="cards">
