import React, { useState, useEffect } from 'react';
import './RealEstateInfo.css';

function RealEstateComparison() {
  const [isVisible, setIsVisible] = useState({});
  const [sortConfig, setSortConfig] = useState({ key: null, direction: 'ascending' });
  const [selectedAreas, setSelectedAreas] = useState(['all']);
  const [viewMode, setViewMode] = useState('table'); // 'table' or 'card'

  // 监听滚动显示动画
  useEffect(() => {
    const handleScroll = () => {
      const sections = document.querySelectorAll('.section-animated');
      const newVisibility = {};

      sections.forEach(section => {
        const rect = section.getBoundingClientRect();
        const id = section.getAttribute('data-section');
        if (rect.top < window.innerHeight * 0.75 && rect.bottom > 0) {
          newVisibility[id] = true;
        }
      });

      setIsVisible(prev => ({ ...prev, ...newVisibility }));
    };

    window.addEventListener('scroll', handleScroll);
    handleScroll(); // 初始检查

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  // 表格排序功能
  const handleSort = (key) => {
    let direction = 'ascending';
    if (sortConfig.key === key && sortConfig.direction === 'ascending') {
      direction = 'descending';
    }
    setSortConfig({ key, direction });
  };

  // 区域选择功能
  const handleAreaToggle = (area) => {
    if (area === 'all') {
      setSelectedAreas(['all']);
    } else {
      setSelectedAreas(prev => {
        // 如果已经选择了'all'，先移除
        if (prev.includes('all')) {
          return [area];
        }
        // 切换选中状态
        if (prev.includes(area)) {
          const newAreas = prev.filter(a => a !== area);
          // 如果没有选中任何区域，默认选中'all'
          return newAreas.length === 0 ? ['all'] : newAreas;
        } else {
          return [...prev, area];
        }
      });
    }
  };

  // 视图模式切换
  const handleViewModeToggle = (mode) => {
    setViewMode(mode);
  };

  // 区域价格数据（用于图表）
  const areaPriceData = [
    { area: '南山区', newPrice: 12.6, resalePrice: 12.0 },
    { area: '福田区', newPrice: 10.6, resalePrice: 10.0 },
    { area: '罗湖区', newPrice: 7.8, resalePrice: 7.2 },
    { area: '龙华区', newPrice: 6.9, resalePrice: 6.3 },
    { area: '宝安区', newPrice: 7.6, resalePrice: 7.1 },
    { area: '龙岗区', newPrice: 5.5, resalePrice: 5.1 },
    { area: '光明区', newPrice: 5.3, resalePrice: 4.9 },
    { area: '坪山区', newPrice: 4.5, resalePrice: 4.2 }
  ];

  return (
    <div className="real-estate-container">
      {/* 返回主页面链接 */}
      <div className="back-link">
        <a href="#" onClick={(e) => {
          e.preventDefault();
          window.location.hash = '#info';
          const event = new CustomEvent('navigateToPage', { detail: { page: 'realestateInfo' } });
          window.dispatchEvent(event);
        }}>
          ← 返回深圳龙华红山片区房产信息
        </a>
      </div>

      <h2>🏘️ 深圳各区域房产对比分析 (2025年)</h2>

      <p className="intro-text">
        深圳不同区域的房产市场各具特色，价格差异较大。以下是2025年深圳主要区域的房价对比、区域特点及购房建议，希望能帮助您找到适合自己的区域。
      </p>

      {/* 区域价格对比图表 */}
      <div className="chart-section section-animated" data-section="chart">
        <h3>📊 深圳各区域房价对比</h3>
        <div className="chart-wrapper">
          <svg viewBox="0 0 800 400" className="area-chart">
            {/* X轴 */}
            <line x1="80" y1="350" x2="750" y2="350" stroke="#cbd5e1" strokeWidth="2" />
            {/* Y轴 */}
            <line x1="80" y1="50" x2="80" y2="350" stroke="#cbd5e1" strokeWidth="2" />

            {/* Y轴刻度 */}
            {[4, 6, 8, 10, 12, 14].map((value, i) => (
              <g key={i}>
