import React, { useState, useEffect } from 'react';
import './RealEstateInfo.css';

function RealEstateInfo() {
  const [isVisible, setIsVisible] = useState({});
  const [sortConfig, setSortConfig] = useState({ key: null, direction: 'ascending' });
  const [filteredType, setFilteredType] = useState('all');

  // 监听滚动显示动画
  useEffect(() => {
    const handleScroll = () => {
      const sections = document.querySelectorAll('.section-animated');
      const newVisibility = {};

      sections.forEach(section => {
        const rect = section.getBoundingClientRect();
        const id = section.getAttribute('data-section');
        if (rect.top < window.innerHeight * 0.75 && rect.bottom > 0) {
          newVisibility[id] = true;
        }
      });

      setIsVisible(prev => ({ ...prev, ...newVisibility }));
    };

    window.addEventListener('scroll', handleScroll);
    handleScroll(); // 初始检查

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  // 表格排序功能
  const handleSort = (key) => {
    let direction = 'ascending';
    if (sortConfig.key === key && sortConfig.direction === 'ascending') {
      direction = 'descending';
    }
    setSortConfig({ key, direction });
  };

  // 过滤功能
  const handleTypeFilter = (type) => {
    setFilteredType(type);
  };

  // 区域价格数据（用于图表）
  const areaPriceData = [
    { area: '红山', price: 6.8 },
    { area: '上塘', price: 6.2 },
    { area: '龙华中心', price: 5.9 },
    { area: '观澜', price: 4.8 },
    { area: '民治', price: 5.5 }
  ];
  return (
    <div className="real-estate-container">
      <h2>🏠 深圳龙华红山片区房产信息 (2025年)</h2>

      {/* 子页面导航 */}
      <div className="subpage-navigation">
        <p>查看更多深圳房产信息：</p>
        <a href="#" className="subpage-link" onClick={(e) => {
          e.preventDefault();
          // 切换到房产对比页面
          window.location.hash = '#comparison';
          const event = new CustomEvent('navigateToPage', { detail: { page: 'realestateComparison' } });
          window.dispatchEvent(event);
        }}>
          🏘️ 深圳各区域房产对比分析
        </a>
      </div>

      <p className="intro-text">
        深圳龙华红山片区是近年来发展较快的区域，配套日益完善。以下是2025年该区域购房和租房的最新费用信息，希望能帮您做参考。
      </p>

      {/* 价格指数卡片 */}
      <div className="price-index-cards">
        <div className="price-index-card">
          <h4>红山片区</h4>
          <div className="price-value">68,000<span className="price-unit">元/㎡</span></div>
          <div className="price-trend negative">↓ 2.5%</div>
          <p className="price-period">近30天</p>
        </div>
        <div className="price-index-card">
          <h4>龙华区</h4>
          <div className="price-value">59,000<span className="price-unit">元/㎡</span></div>
          <div className="price-trend negative">↓ 1.2%</div>
          <p className="price-period">近30天</p>
        </div>
        <div className="price-index-card">
          <h4>深圳市</h4>
          <div className="price-value">71,500<span className="price-unit">元/㎡</span></div>
          <div className="price-trend negative">↓ 0.8%</div>
          <p className="price-period">近30天</p>
        </div>
      </div>

      {/* 区域价格对比图表 */}
      <div className="chart-section section-animated" data-section="chart">
        <h3>📊 龙华区各板块房价对比</h3>
        <div className="chart-wrapper">
          <svg viewBox="0 0 600 300" className="area-chart">
            {/* X轴 */}
            <line x1="50" y1="250" x2="550" y2="250" stroke="#cbd5e1" strokeWidth="2" />
            {/* Y轴 */}
            <line x1="50" y1="50" x2="50" y2="250" stroke="#cbd5e1" strokeWidth="2" />

            {/* Y轴刻度 */}
            {[4, 5, 6, 7].map((value, i) => (
              <g key={i}>
                <line x1="48" y1={250 - (value - 4) * 50} x2="52" y2={250 - (value - 4) * 50} stroke="#cbd5e1" strokeWidth="1" />
                <text x="35" y={250 - (value - 4) * 50 + 5} textAnchor="middle" fill="#64748b" fontSize="12">{value}</text>
              </g>
            ))}

            {/* 柱状图 */}
            {areaPriceData.map((item, index) => {
              const x = 100 + index * 80;
              const height = (item.price - 4) * 50;
              return (
                <g key={index}>
                  <rect x={x - 20} y={250 - height} width="40" height={height} fill="#4f46e5" rx="4" />
                  <text x={x} y={250 - height - 10} textAnchor="middle" fill="#4f46e5" fontWeight="600">{item.price}</text>
                  <text x={x} y="270" textAnchor="middle" fill="#64748b" fontSize="12">{item.area}</text>
                </g>
              );
            })}

            {/* 图例 */}
            <text x="300" y="30" textAnchor="middle" fill="#475569" fontSize="14" fontWeight="600">均价（万/㎡）</text>
          </svg>
        </div>
      </div>

      {/* 主要楼盘信息表格 */}
      <div className="table-section section-animated" data-section="table">
        <div className="table-controls">
          <h3>🏘️ 主要楼盘信息</h3>
          <div className="filter-buttons">
            <button 
              className={`filter-btn ${filteredType === 'all' ? 'active' : ''}`}
              onClick={() => handleTypeFilter('all')}
            >
              全部类型
            </button>
            <button 
              className={`filter-btn ${filteredType === 'new' ? 'active' : ''}`}
              onClick={() => handleTypeFilter('new')}
            >
              新房
            </button>
            <button 
              className={`filter-btn ${filteredType === 'resale' ? 'active' : ''}`}
              onClick={() => handleTypeFilter('resale')}
            >
              二手房
            </button>
          </div>
        </div>

        <div className="table-container">
          <table className="estate-table">
            <thead>
              <tr>
                <th onClick={() => handleSort('name')} className="sortable">
                  小区名称 {sortConfig.key === 'name' && (sortConfig.direction === 'ascending' ? '↑' : '↓')}
                </th>
                <th onClick={() => handleSort('type')} className="sortable">
                  类型 {sortConfig.key === 'type' && (sortConfig.direction === 'ascending' ? '↑' : '↓')}
                </th>
                <th>主推户型</th>
                <th onClick={() => handleSort('price')} className="sortable">
                  购房价格（约） {sortConfig.key === 'price' && (sortConfig.direction === 'ascending' ? '↑' : '↓')}
                </th>
                <th>租房价格（约）</th>
                <th>核心特点</th>
              </tr>
            </thead>
            <tbody>
              {(() => {
                // 原始数据
                const tableData = [
                  { name: '尚誉红山里', type: '住宅', houseType: '72-99㎡ 2-3房', price: '435-630万（单价5.95-6.3万/㎡）', rent: '7500元/月（99㎡）', features: '<span className="feature">现房</span>，龙华实验学校学区，折扣力度较大', typeFilter: 'new' },
                  { name: '联发臻著雅居', type: '住宅', houseType: '81-91㎡ 3房', price: '81平约510万起，91平约560万起（工抵房83折后）', rent: '暂无数据', features: '限价房，近龙华实验学校，高得房率', typeFilter: 'new' },
                  { name: '众福红山印', type: '住宅', houseType: '79-100㎡ 3-4房', price: '537万起（单价6.8万/㎡起）', rent: '暂无数据', features: '低容积率，近深圳高级中学（集团）北校区，近规划地铁27号线民宝站（规划中）', typeFilter: 'new' },
                  { name: '红山华府', type: '住宅', houseType: '63-78㎡ 2-3房', price: '470万起（折后）', rent: '暂无数据', features: '<span className="feature">深高北学区</span>，2025年4月"日光"盘，近地铁4/6号线红山站（约450米）', typeFilter: 'new' },
                  { name: '华业玫瑰四季', type: '住宅', houseType: '89㎡ 3房', price: '暂无数据（二手房视市场而定）', rent: '7300元/月', features: '龙华核心地段，近红山地铁站，周边商业配套成熟', typeFilter: 'resale' },
                  { name: '天曜府', type: '住宅', houseType: '104-185㎡ 4-5房', price: '暂无最新数据', rent: '8800-15500元/月', features: '中高端项目，紧邻龙华区实验学校，自带幼儿园，2025年12月底精装交付', typeFilter: 'new' }
                ];

                // 过滤数据
                let filteredData = tableData;
                if (filteredType !== 'all') {
                  filteredData = tableData.filter(item => item.typeFilter === filteredType);
                }

                // 排序数据
                if (sortConfig.key !== null) {
                  filteredData.sort((a, b) => {
                    if (a[sortConfig.key] < b[sortConfig.key]) {
                      return sortConfig.direction === 'ascending' ? -1 : 1;
                    }
                    if (a[sortConfig.key] > b[sortConfig.key]) {
                      return sortConfig.direction === 'ascending' ? 1 : -1;
                    }
                    return 0;
                  });
                }

                // 渲染数据
                return filteredData.map((item, index) => (
                  <tr key={index}>
                    <td className="highlight">{item.name}</td>
                    <td>{item.type}</td>
                    <td>{item.houseType}</td>
                    <td>{item.price}</td>
                    <td>{item.rent}</td>
                    <td dangerouslySetInnerHTML={{ __html: item.features }} />
                  </tr>
                ));
              })()}
            </tbody>
          </table>
        </div>
      </div>

      {/* 购房相关费用 */}
      <div className="cost-section section-animated" data-section="buyingCosts">
        <h3>🏠 购房相关费用</h3>
        <p>除了房价本身，购房还需准备一些其他费用：</p>
        <ul className="cost-list">
          <li><strong>首付款</strong>：根据政策，首套房首付比例通常不低于30%。以总价500万的房子为例，首付约需要<strong>150万</strong>左右。</li>
          <li>
            <strong>税费</strong>：
            <ul className="sub-list">
              <li><strong>契税</strong>：首套房90㎡以下为总价的1%，90㎡以上为1.5%；二套房为1%-3%不等。</li>
              <li><strong>增值税及附加</strong>：房产证未满两年或非普通住宅需缴纳，约占差额的5.6%左右。</li>
              <li><strong>个人所得税</strong>：通常为总价的1%或差额的20%。</li>
              <li><strong>登记费、印花税</strong>等零星费用。</li>
            </ul>
          </li>
          <li><strong>公共维修基金</strong>：约为总房款的2%-3%，用于未来小区的公共部位、共用设施的维修更新。</li>
          <li><strong>月供</strong>：以贷款350万、30年期限、LPR利率计算，月供大约在<strong>1.7万-1.9万元</strong>左右。</li>
        </ul>
      </div>

      {/* 租房相关费用 */}
      <div className="cost-section section-animated" data-section="rentingCosts">
        <h3>🧳 租房相关费用</h3>
        <p>租房时的成本主要包括：</p>
        <ul className="cost-list">
          <li><strong>租金押金</strong>：通常为"押二付一"或"押一付一"，即相当于2个月或1个月的租金作为押金，另支付第一个月的租金。</li>
          <li><strong>中介费</strong>：传统中介通常收取半个月或一个月的租金作为中介费。不过，也有平台提供"<strong>0元中介费</strong>"的服务，特别是针对应届或毕业一年内的大学生。</li>
          <li><strong>水电煤气、物业管理费、网络费</strong>等日常开销：这些费用通常由租客承担，具体金额因小区和使用情况而异。</li>
        </ul>
      </div>

      {/* 如何选择：购房还是租房 */}
      <div className="guide-section section-animated" data-section="choosingGuide">
        <h3>💡 如何选择：购房还是租房？</h3>
        <div className="guide-grid">
          <div className="guide-card">
            <div className="guide-icon">📚</div>
            <h4>教育需求优先</h4>
            <p>如果非常看重教育资源，需要仔细研究<strong>学区划分</strong>（以教育局最新公布为准）和<strong>入学积分</strong>政策。例如，尚誉红山里对应的龙华区实验学校2025年小一录取积分较高（深户+学区房+社保满10年达102.5分），且学位紧张。</p>
          </div>
          <div className="guide-card">
            <div className="guide-icon">🚇</div>
            <h4>通勤与交通便利性</h4>
            <p>红山片区有地铁4号线、6号线，红山站、上塘站是主要站点。规划中的地铁线路（如22号线、27号线）未来会进一步提升出行便利。</p>
          </div>
          <div className="guide-card">
            <div className="guide-icon">💰</div>
            <h4>财务能力评估</h4>
            <p><strong>购房</strong>：需要评估首付能力、月供承受力（通常要求月收入是月供的2倍以上）以及长期定居的打算。<strong>租房</strong>：更适合预算有限、工作生活可能变动，或者尚未决定是否长期在该区域发展的人。</p>
          </div>
          <div className="guide-card">
            <div className="guide-icon">🏠</div>
            <h4>居住品质与偏好</h4>
            <p>新房或次新房（如尚誉红山里、天曜府）在户型设计、小区环境等方面可能更现代。租房也可以体验到不同小区的居住环境，方便未来决策。</p>
          </div>
        </div>
      </div>

      {/* 重要提示 */}
      <div className="tips-section section-animated" data-section="tips">
        <h3>⚠️ 重要提示</h3>
        <ul className="tips-list">
          <li>表中新房价格通常来自开发商宣传，二手房和租房价格多为业主挂牌价，<strong>实际交易价格可能有所浮动</strong>。二手房交易还需特别关注税费情况（如房产证是否"满五唯一"）。</li>
          <li>对于<strong>学区房</strong>，教育政策及学区划分可能存在调整，务必须以教育局及学校当年发布的最新官方信息为准。</li>
          <li>购房和租房都是大事，建议<strong>务必实地考察</strong>心仪楼盘及周边环境，并咨询专业的房产顾问或律师。</li>
        </ul>
      </div>

      <div className="conclusion">
        <p>
          希望这些信息能帮助您更好地权衡在龙华红山片区安家的方式。根据您的家庭规划、财务状况和生活需求，做出最适合自己的选择。
        </p>
      </div>
    </div>
  );
}

export default RealEstateInfo;
