import React, { useState, useEffect } from 'react';
import './App.css';
import GomokuGame from './components/GomokuGame';
import SnakeGame from './components/SnakeGame';
import TankGame from './components/TankGame';
import TravelPlan from './components/TravelPlan';

function App() {
  // 登录状态管理
  const [isLoggedIn, setIsLoggedIn] = useState(true); // 默认已登录
  const [isGuest, setIsGuest] = useState(true); // 默认是游客
  const [currentUser, setCurrentUser] = useState(null);
  const [loginEmail, setLoginEmail] = useState('');
  const [loginPassword, setLoginPassword] = useState('');
  const [loginError, setLoginError] = useState('');

  // 状态管理：任务列表和输入框的值
  const [tasks, setTasks] = useState([]);
  const [inputValue, setInputValue] = useState('');
  // 新增：历史记录状态
  const [taskHistory, setTaskHistory] = useState([]);
  // 新增：显示/隐藏历史记录的状态
  const [showHistory, setShowHistory] = useState(false);
  // 新增：当前激活的页面
  const [activePage, setActivePage] = useState('jokes'); // 'jokes', 'todo', 'analytics' 或 'games' - 笑话页面作为首页
  // 新增：控制登录弹窗显示
  const [showLogin, setShowLogin] = useState(false);
  // 新增：笑话列表状态
  const [jokes, setJokes] = useState([]);
  // 新增：当前显示的笑话页码
  const [currentPage, setCurrentPage] = useState(1);
  // 每页显示的笑话数量
  const jokesPerPage = 10;
  // 新增：控制侧边栏显示状态（移动端适配）
  const [showSidebar, setShowSidebar] = useState(false);

  // 计算分析数据
  const totalTasks = tasks.length;
  const completedTasks = tasks.filter(task => task.completed).length;
  const activeTasks = tasks.filter(task => !task.completed).length;
  const deletedTasks = taskHistory.length;
  const completionRate = totalTasks > 0 ? Math.round((completedTasks / totalTasks) * 100) : 0;

  // 处理添加任务
  const handleAddTask = () => {
    if (inputValue.trim() !== '') {
      setTasks([...tasks, {
        id: Date.now(),
        text: inputValue,
        completed: false
      }]);
      setInputValue('');
    }
  };

  // 处理删除任务
  const handleDeleteTask = (id) => {
    const taskToDelete = tasks.find(task => task.id === id);
    if (taskToDelete) {
      // 添加到历史记录，包含删除时间
      setTaskHistory([...taskHistory, {
        ...taskToDelete,
        deletedAt: new Date().toLocaleString()
      }]);
      // 从当前任务列表中删除
      setTasks(tasks.filter(task => task.id !== id));
    }
  };

  // 新增：清空历史记录
  const handleClearHistory = () => {
    setTaskHistory([]);
  };

  // 新增：从历史记录中恢复任务
  const handleRestoreTask = (id) => {
    const taskToRestore = taskHistory.find(task => task.id === id);
    if (taskToRestore) {
      // 从历史记录中移除
      setTaskHistory(taskHistory.filter(task => task.id !== id));
      // 添加回任务列表，重置完成状态
      setTasks([...tasks, {
        ...taskToRestore,
        completed: false,
        id: Date.now() // 生成新的ID，避免冲突
      }]);
    }
  };

  // 处理任务完成状态切换
  const handleToggleComplete = (id) => {
    setTasks(tasks.map(task => 
      task.id === id ? { ...task, completed: !task.completed } : task
    ));
  };

  // 处理回车键添加任务
  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleAddTask();
    }
  }

  // 登录处理函数（模拟验证）
  const handleLogin = (e) => {
    e.preventDefault();

    // 简单的模拟验证逻辑
    // 实际应用中应该调用API或连接数据库进行验证
    if (loginEmail && loginPassword) {
      // 假设任何非空的邮箱和密码都是有效的
      setCurrentUser({ email: loginEmail });
      setIsLoggedIn(true);
      setIsGuest(false); // 登录后不再是游客
      setLoginError('');
      setLoginEmail('');
      setLoginPassword('');
      setShowLogin(false);

      // 模拟加载用户数据（这里可以根据用户ID加载特定的任务数据）
      loadUserData();
    } else {
      setLoginError('请输入邮箱和密码');
    }
  };

  // 游客登录处理
  const handleGuestLogin = () => {
    setIsLoggedIn(true);
    setIsGuest(true);
    setCurrentUser(null);

    // 加载游客默认数据
    setTasks([
      { id: Date.now() - 10000, text: '欢迎使用待办清单！', completed: false },
      { id: Date.now() - 20000, text: '请登录以保存您的任务数据', completed: false }
    ]);
    setTaskHistory([]);
  };

  // 注销处理函数
  const handleLogout = () => {
    setIsLoggedIn(true); // 注销后仍保持登录状态
    setIsGuest(true); // 切换为游客模式
    setCurrentUser(null);
    setTasks([]);
    setTaskHistory([]);

    // 加载游客默认数据
    setTimeout(() => {
      setTasks([
        { id: Date.now() - 10000, text: '您已切换到游客模式', completed: false },
        { id: Date.now() - 20000, text: '请登录以保存您的任务数据', completed: false }
      ]);
    }, 100);
  };

  // 模拟加载用户数据
  const loadUserData = () => {
    // 在实际应用中，这里应该从API或本地存储加载特定用户的任务数据
    // 这里仅作为示例，加载一些模拟数据
    setTasks([
      { id: Date.now() - 10000, text: '欢迎登录！这是你的第一个任务', completed: false },
      { id: Date.now() - 20000, text: '点击任务文本或按钮可以标记完成', completed: false }
    ]);
    setTaskHistory([]);
  };

  // 生成随机笑话的函数 - 确保100条笑话内容唯一
  const generateRandomJokes = () => {
    // 扩展笑话模板库，确保有足够多的唯一笑话
    const jokeTemplates = [
      "为什么程序员总是分不清万圣节和圣诞节？因为 Oct 31 = Dec 25。",
      "程序员的墓志铭：while (alive) { code(); sleep(); }",
      "什么是程序员最喜欢的音乐？Java script。",
      "为什么程序员不喜欢大自然？因为它有太多的虫子。",
      "程序员和女朋友的对话：你能解释什么是无限循环吗？女朋友：就是你给我解释完什么是无限循环后，我又问你什么是无限循环。",
      "程序员的妻子说：去买一斤包子，如果看到卖西瓜的，买一个。程序员买了一个包子回来，妻子问为什么只买一个，他说：因为我看到卖西瓜的了。",
      "为什么程序员喜欢黑暗模式？因为光明会带来bugs。",
      "两个程序员走进酒吧，第一个说：我要一杯啤酒。第二个说：我也要一杯啤酒。然后整个酒吧都崩溃了。",
      "程序员的周末计划：周五晚上写代码，周六早上修周五晚上的代码，周日下午继续修周六早上的代码。",
      "程序员的三个阶段：1. 觉得自己什么都懂；2. 觉得自己什么都不懂；3. 觉得自己什么都懂，但意识到自己不知道的更多。",
      "为什么程序员总是混淆万圣节和圣诞节？因为 Oct 31 = Dec 25。",
      "一个SQL语句走进酒吧，要了一杯啤酒。酒保说：抱歉，我们不服务子查询。",
      "什么是程序员的理想约会？两个人一起写代码，然后找出对方的bug。",
      "为什么程序员总是随身携带铅笔？因为他们喜欢做标记。",
      "程序员如何安慰哭泣的孩子？别哭了，我给你写个递归函数。",
      "为什么程序员不喜欢电梯？因为他们总是担心堆栈溢出。",
      "什么是程序员的最佳表白方式？我对你的爱就像无限循环，永远不会结束。",
      "程序员如何看待爱情？爱情就像变量，有时候你以为它是引用类型，结果却是值类型。",
      "为什么程序员总是迟到？因为他们分不清UTC时间和本地时间。",
      "什么是程序员的恶梦？打开一个没有任何注释的大型代码库。",
      "程序员的口头禅：这在我的机器上运行得很好！",
      "为什么程序员喜欢写注释？因为他们知道自己一个月后也看不懂自己的代码。",
      "什么是程序员的社交技能？Ctrl+C和Ctrl+V。",
      "程序员如何表达爱意？我对你的爱就像while(1)循环，永远不会停止。",
      "为什么程序员不喜欢喝咖啡？因为他们更喜欢喝代码。",
      "什么是程序员的周末？修完所有的bug，然后发现更多的bug。",
      "程序员的婚姻观：爱情就像多线程编程，需要同步和互斥。",
      "为什么程序员总是戴眼镜？因为他们要仔细看代码。",
      "什么是程序员的音乐品味？只有二进制音乐：0和1。",
      "程序员如何庆祝生日？写一个递归函数来计算自己的年龄。",
      "为什么程序员不喜欢跑步？因为他们怕栈溢出。",
      "什么是程序员的宠物？一只叫做Bug的猫。",
      "程序员的假期计划：在家里写代码，然后发现自己需要更多的假期来修复代码。",
      "为什么程序员总是分不清左右？因为他们只知道0和1。",
      "什么是程序员的幸运数字？42（因为答案是42）。",
      "程序员的理财观：钱就像内存，不用就会被浪费。",
      "为什么程序员不喜欢看电影？因为他们更喜欢debug。",
      "什么是程序员的理想工作环境？一个安静的房间，一台电脑，和无限的咖啡。",
      "程序员如何教育孩子？从小教他们写Hello World。",
      "为什么程序员总是忘记吃饭？因为他们在写代码的时候进入了心流状态。",
      "什么是程序员的超级power？能够在脑海中运行代码。",
      "程序员的健康观：久坐不利于健康，但写代码的时候根本顾不上。",
      "为什么程序员不喜欢旅游？因为他们觉得家里的电脑最舒服。",
      "什么是程序员的浪漫？两个人一起写代码，然后互相赞美对方的代码优雅。",
      "程序员的时间观：时间就像变量，过得太快了。",
      "为什么程序员总是穿拖鞋？因为他们不想被束缚。",
      "什么是程序员的艺术？用最少的代码做最多的事情。",
      "程序员的哲学观：生活就像代码，充满了bug和惊喜。",
      "为什么程序员不喜欢玩游戏？因为他们觉得写游戏比玩游戏更有趣。",
      "什么是程序员的信仰？代码就是真理。",
      "程序员的人生观：人生就像一个大型项目，需要不断迭代和优化。",
      "为什么程序员不喜欢聚会？因为他们觉得和电脑相处更舒服。",
      "什么是程序员的幽默？只有程序员才能理解的梗。",
      "程序员的价值观：效率至上，代码为王。",
      "为什么程序员总是熬夜？因为晚上写代码效率更高。",
      "什么是程序员的骄傲？写出一段没有bug的代码。",
      "程序员的友谊：一起写代码，一起debug，一起成长。",
      "为什么程序员不喜欢打电话？因为他们更喜欢发邮件或者IM。",
      "什么是程序员的目标？写出改变世界的代码。",
      "程序员的坚持：bug不解决，绝不睡觉。",
      "为什么程序员总是单身？因为他们觉得代码比约会更有趣。",
      "什么是程序员的恐惧？代码被重构，自己的工作被取代。",
      "程序员的智慧：知道什么时候该重构代码，什么时候该重写。",
      "为什么程序员不喜欢开会？因为他们觉得开会浪费时间。",
      "什么是程序员的成就感？解决一个困扰已久的bug。",
      "程序员的学习观：学无止境，技术更新太快了。",
      "为什么程序员总是戴耳机？因为他们需要专注。",
      "什么是程序员的财富？知识和经验。",
      "程序员的挑战：不断学习新技术，跟上时代的步伐。",
      "为什么程序员不喜欢逛街？因为他们觉得网上购物更方便。",
      "什么是程序员的自由？能够选择自己喜欢的编程语言和工具。",
      "程序员的热情：对技术的热爱，对解决问题的执着。",
      "为什么程序员总是写博客？因为他们想分享自己的知识和经验。",
      "什么是程序员的幸福？写出一段漂亮、高效、可维护的代码。",
      "程序员的选择：在效率和可读性之间找到平衡。",
      "为什么程序员总是用键盘快捷键？因为他们觉得鼠标太慢了。",
      "什么是程序员的团队精神？互相帮助，共同进步。",
      "程序员的成长：从写Hello World到构建大型系统。",
      "为什么程序员不喜欢广告？因为他们觉得广告打扰了他们的专注。",
      "什么是程序员的勇气？敢于重构自己的代码。",
      "程序员的责任：写出安全、可靠、高效的代码。",
      "为什么程序员总是用英文写注释？因为技术术语大多是英文的。",
      "什么是程序员的耐心？一遍又一遍地调试代码，直到找到问题所在。",
      "程序员的创新：用新的方法解决旧的问题。",
      "为什么程序员总是争论哪种编程语言最好？因为每种语言都有自己的优点和缺点。",
      "什么是程序员的眼光？能够预见未来技术的发展趋势。",
      "程序员的梦想：创造出改变世界的产品。",
      "为什么程序员总是关注性能？因为性能是用户体验的重要组成部分。",
      "什么是程序员的洞察力？能够快速发现问题的本质。",
      "程序员的追求：不断提高自己的编程技能。",
      "为什么程序员总是备份代码？因为他们知道数据丢失的痛苦。",
      "什么是程序员的严谨？写代码的时候考虑所有可能的边界情况。",
      "程序员的分享：开源精神，让世界变得更美好。",
      "为什么程序员总是优化代码？因为他们追求完美。",
      "什么是程序员的直觉？能够感觉到哪里可能有bug。"
    ];

    // 确保我们有足够的唯一笑话，如果模板不够，就生成变体
    const uniqueJokes = [];
    const usedJokes = new Set();

    // 首先使用原始模板
    for (let i = 0; i < jokeTemplates.length && uniqueJokes.length < 100; i++) {
      const template = jokeTemplates[i];
      if (!usedJokes.has(template)) {
        usedJokes.add(template);
        uniqueJokes.push({
          id: uniqueJokes.length + 1,
          content: template,
          createdAt: new Date(Date.now() - (99 - uniqueJokes.length) * 86400000).toLocaleDateString() // 均匀分布日期
        });
      }
    }

    // 如果原始模板不够，生成一些变体来达到100条
    const variations = ['😂', '🤣', '😄', '😆', '😊', '😉', '😎', '🤓', '😜', '🤪'];
    while (uniqueJokes.length < 100) {
      const randomIndex = Math.floor(Math.random() * jokeTemplates.length);
      const baseJoke = jokeTemplates[randomIndex];
      const randomVariation = variations[Math.floor(Math.random() * variations.length)];
      const newJoke = `${baseJoke} ${randomVariation}`;

      if (!usedJokes.has(newJoke)) {
        usedJokes.add(newJoke);
        uniqueJokes.push({
          id: uniqueJokes.length + 1,
          content: newJoke,
          createdAt: new Date(Date.now() - (99 - uniqueJokes.length) * 86400000).toLocaleDateString() // 均匀分布日期
        });
      }
    }

    return uniqueJokes;
  };

  // 页面加载时生成笑话
  useEffect(() => {
    setJokes(generateRandomJokes());
  }, []);

  // 分页逻辑
  const indexOfLastJoke = currentPage * jokesPerPage;
  const indexOfFirstJoke = indexOfLastJoke - jokesPerPage;
  const currentJokes = jokes.slice(indexOfFirstJoke, indexOfLastJoke);
  const totalPages = Math.ceil(jokes.length / jokesPerPage);

  // 处理页码变更
  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
    // 滚动到页面顶部
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  return (
    <div className="app-wrapper">
      {/* 主应用内容 - 默认显示 */}
      <div className="main-layout">
        {/* 移动端汉堡菜单按钮 */}
        <button 
          className="mobile-menu-btn"
          onClick={() => setShowSidebar(!showSidebar)}
          aria-label="切换菜单"
        >
          ☰
        </button>

        {/* 左侧菜单 */}
        <aside className={`sidebar ${showSidebar ? 'show' : ''}`}>
          {/* 移动端关闭按钮 */}
          <button 
            className="mobile-close-btn"
            onClick={() => setShowSidebar(false)}
            aria-label="关闭菜单"
          >
            ×
          </button>
          <div className="logo">待办清单</div>
          <nav className="sidebar-nav">
            <button 
              className={`sidebar-btn ${activePage === 'jokes' ? 'active' : ''}`}
              onClick={() => setActivePage('jokes')}
            >
              笑话管理
            </button>
            <button 
              className={`sidebar-btn ${activePage === 'todo' ? 'active' : ''}`}
              onClick={() => setActivePage('todo')}
            >
              任务列表
            </button>
            <button 
              className={`sidebar-btn ${activePage === 'analytics' ? 'active' : ''}`}
              onClick={() => setActivePage('analytics')}
            >
              数据分析
            </button>
            <button 
              className={`sidebar-btn ${activePage === 'games' ? 'active' : ''}`}
              onClick={() => setActivePage('games')}
            >
              游戏中心
            </button>
            <button 
              className={`sidebar-btn ${activePage === 'travel' ? 'active' : ''}`}
              onClick={() => setActivePage('travel')}
            >
              旅游计划
            </button>
            <button className="sidebar-btn">
              设置
            </button>
          </nav>
        </aside>

        {/* 顶部导航栏 - 从content中剥离出来 */}
        <div className="nav-buttons-with-user">
          <div className="nav-buttons">
            <button 
              className={`nav-btn ${activePage === 'jokes' ? 'active' : ''}`}
              onClick={() => setActivePage('jokes')}
            >
              笑话管理
            </button>
            <button 
              className={`nav-btn ${activePage === 'todo' ? 'active' : ''}`}
              onClick={() => setActivePage('todo')}
            >
              任务列表
            </button>
            <button 
              className={`nav-btn ${activePage === 'analytics' ? 'active' : ''}`}
              onClick={() => setActivePage('analytics')}
            >
              数据分析
            </button>
            <button 
              className={`nav-btn ${activePage === 'games' ? 'active' : ''}`}
              onClick={() => setActivePage('games')}
            >
              游戏中心
            </button>
            <button 
              className={`nav-btn ${activePage === 'travel' ? 'active' : ''}`}
              onClick={() => setActivePage('travel')}
            >
              旅游计划
            </button>
        </div>

          {/* 用户信息 */}
          <div className="user-info">
            {isGuest ? (
              <>
                <span className="user-email">游客</span>
                <button className="login-btn-small" onClick={() => setShowLogin(true)}>
                  登录
                </button>
              </>
            ) : (
              <>
                <span className="user-email">{currentUser.email}</span>
                <button className="logout-btn" onClick={handleLogout}>
                  注销
                </button>
              </>
            )}
          </div>
        </div>

        {/* 主内容区域 */}
        <main className="content">
          {/* 笑话管理页面 - 作为首页 */}
          {activePage === 'jokes' && (
            <div className="jokes-container">
              <h2>笑话管理</h2>
              <p className="jokes-count">共有 {jokes.length} 条笑话</p>

              <div className="jokes-list">
                {currentJokes.map(joke => (
                  <div key={joke.id} className="joke-item">
                    <div className="joke-content">{joke.content}</div>
                    <div className="joke-meta">
                      <span className="joke-id">#{joke.id}</span>
                      <span className="joke-date">{joke.createdAt}</span>
                    </div>
                  </div>
                ))}
              </div>

              {/* 分页控件 */}
              <div className="pagination">
                <button 
                  className="page-btn" 
                  onClick={() => handlePageChange(currentPage - 1)} 
                  disabled={currentPage === 1}
                >
                  上一页
                </button>
                <div className="page-numbers">
                  {Array.from({ length: totalPages }, (_, index) => index + 1)
                    .filter(page => 
                      page === 1 || 
                      page === totalPages || 
                      (page >= currentPage - 2 && page <= currentPage + 2)
                    )
                    .map((page, i, array) => {
                      // 处理页码之间的省略号
                      if (i > 0 && array[i] - array[i-1] > 1) {
                        return (
                          <span key={`ellipsis-${i}`} className="page-ellipsis">...</span>
                        );
                      }
                      return (
                        <button
                          key={page}
                          className={`page-btn ${currentPage === page ? 'active' : ''}`}
                          onClick={() => handlePageChange(page)}
                        >
                          {page}
                        </button>
                      );
                    })}
                </div>
                <button 
                  className="page-btn" 
                  onClick={() => handlePageChange(currentPage + 1)} 
                  disabled={currentPage === totalPages}
                >
                  下一页
                </button>
              </div>
            </div>
          )}

          {/* 任务列表页面 */}
          {activePage === 'todo' && (
            <>
              <div className="input-container">
                <input
                  type="text"
                  value={inputValue}
                  onChange={(e) => setInputValue(e.target.value)}
                  onKeyPress={handleKeyPress}
                  placeholder="添加新任务..."
                />
                <button onClick={handleAddTask}>添加</button>
              </div>

              <ul className="task-list">
                {tasks.map(task => (
                  <li key={task.id} className={task.completed ? 'completed' : ''}>
                    <span className="task-text">{task.text}</span>
                    <div className="task-buttons">
                       <button 
                        className={`complete-btn ${task.completed ? 'completed' : ''}`} 
                        onClick={() => handleToggleComplete(task.id)}
                        aria-label={task.completed ? '取消完成任务' : '标记任务为已完成'}
                      >
                        {task.completed ? '已完成' : '标记完成'}
                      </button>
                      <button 
                        className="delete-btn"
                        onClick={() => handleDeleteTask(task.id)}
                        aria-label="删除任务"
                      >
                        删除
                      </button>
                    </div>
                  </li>
                ))}
              </ul>

              {/* 历史记录功能 - 只在任务列表页面显示 */}
              <div className="history-section">
                <button 
                  className="history-toggle"
                  onClick={() => setShowHistory(!showHistory)}
                >
                  {showHistory ? '隐藏历史记录' : '查看历史记录'}
                </button>

                {showHistory && (
                  <div className="history-content">
                    <h3>已删除任务 ({taskHistory.length})</h3>
                    {taskHistory.length > 0 && (
                      <button className="clear-history-btn" onClick={handleClearHistory}>
                        清空历史记录
                      </button>
                    )}
                    <ul className="history-list">
                      {taskHistory.map(task => (
                        <li key={task.id} className="history-item">
                          <div className="history-info">
                            <span className="history-text">{task.text}</span>
                            <span className="history-time">删除于: {task.deletedAt}</span>
                          </div>
                          <div className="history-actions">
                            <button className="restore-btn" onClick={() => handleRestoreTask(task.id)}>
                              恢复
                            </button>
                          </div>
                        </li>
                      ))}
                    </ul>
                    {taskHistory.length === 0 && (
                      <p className="empty-history">暂无历史记录</p>
                    )}
                  </div>
                )}
              </div>
            </>
          )}

          {/* 数据分析页面 */}
          {activePage === 'analytics' && (
            <div className="analytics-container">
              <h2>任务数据分析</h2>
              <div className="stats-grid">
                <div className="stat-card">
                  <h3>总任务数</h3>
                  <p className="stat-number">{totalTasks}</p>
                </div>
                <div className="stat-card">
                  <h3>已完成任务</h3>
                  <p className="stat-number">{completedTasks}</p>
                </div>
                <div className="stat-card">
                  <h3>待处理任务</h3>
                  <p className="stat-number">{activeTasks}</p>
                </div>
                <div className="stat-card">
                  <h3>已删除任务</h3>
                  <p className="stat-number">{deletedTasks}</p>
                </div>
              </div>

              <div className="progress-section">
                <h3>完成率</h3>
                <div className="progress-bar">
                  <div 
                    className="progress-fill" 
                    style={{ width: `${completionRate}%` }}
                  ></div>
                </div>
                <p className="progress-text">{completionRate}%</p>
              </div>

              <div className="chart-placeholder">
                <h3>任务趋势（模拟数据）</h3>
                <div className="bar-chart">
                  {['周一', '周二', '周三', '周四', '周五', '周六', '周日'].map((day, index) => (
                    <div key={day} className="chart-bar">
                      <div className="bar" style={{ height: `${Math.floor(Math.random() * 10) + 1}0px` }}></div>
                      <span>{day}</span>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          )}

          {/* 游戏管理页面 */}
          {activePage === 'games' && (
            <div className="games-container">
              <h2>游戏中心</h2>
              <div className="game-selection">
                <button 
                  className="game-btn" 
                  onClick={() => document.getElementById('gomoku-game').classList.toggle('hidden')}
                >
                  五子棋游戏
                </button>
                <button 
                  className="game-btn" 
                  onClick={() => document.getElementById('snake-game').classList.toggle('hidden')}
                >
                  贪吃蛇游戏
                </button>
                <button 
                  className="game-btn" 
                  onClick={() => document.getElementById('tank-game').classList.toggle('hidden')}
                >
                  坦克大战游戏
                </button>
              </div>

              {/* 五子棋游戏 */}
              <div id="gomoku-game" className="game-wrapper hidden">
                <GomokuGame />
              </div>

              {/* 贪吃蛇游戏 */}
              <div id="snake-game" className="game-wrapper hidden">
                <SnakeGame />
              </div>

              {/* 坦克大战游戏 */}
              <div id="tank-game" className="game-wrapper hidden">
                <TankGame />
              </div>
            </div>
          )}

          {/* 旅游计划页面 */}
          {activePage === 'travel' && (
            <TravelPlan />
          )}
        </main>
      </div>

      {/* 登录弹窗 */}
      {showLogin && (
        <div className="login-modal-overlay" onClick={() => setShowLogin(false)}>
          <div className="login-modal" onClick={(e) => e.stopPropagation()}>
            <div className="login-container">
              <div className="logo">待办清单</div>
              <h1>To-Do List</h1>
              <div className="login-form">
                <h2>登录</h2>
                <form onSubmit={handleLogin}>
                  <div className="form-group">
                    <label htmlFor="email">邮箱</label>
                    <input
                      type="email"
                      id="email"
                      value={loginEmail}
                      onChange={(e) => setLoginEmail(e.target.value)}
                      placeholder="请输入邮箱"
                      required
                    />
                  </div>
                  <div className="form-group">
                    <label htmlFor="password">密码</label>
                    <input
                      type="password"
                      id="password"
                      value={loginPassword}
                      onChange={(e) => setLoginPassword(e.target.value)}
                      placeholder="请输入密码"
                      required
                    />
                  </div>
                  {loginError && <div className="error-message">{loginError}</div>}
                  <button type="submit" className="login-btn">登录</button>
                </form>
                <p className="demo-info">
                  <strong>演示说明：</strong>输入任意非空的邮箱和密码即可登录
                </p>
                <button className="close-login-btn" onClick={() => setShowLogin(false)}>
                  关闭
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
